INSERT INTO `wp_posts` VALUES(93, 1, '2013-01-15 17:33:41', '2013-01-15 16:33:41', '17 minutes - VOSTF\r\n\r\nLe corps d’un travesti décédé est préparé\r\n\r\npour l’enterrement par d’autres travestis.\r\n\r\nSon corps de femme est amené à sa famille,\r\n\r\nqui décide de l’enterrer comme un homme.\r\n\r\nUne procession de travestis se met en marche\r\n\r\nvers la veillée funèbre pour revendiquer\r\n\r\nl’identité construite de la défunte.', 'Os sapatos de Aristeu - René Guerra Luiz - Brésil - 2008', '', 'inherit', 'open', 'open', '', '29-revision-2', '', '', '2013-01-15 17:33:41', '2013-01-15 16:33:41', '', 29, 'http://localhost:8888/?p=93', 0, 'revision', '', 0);
